  process.exit(1);
}

if (!OUTPUT_FILE_PATH) {
  console.error('Error: OUTPUT_FILE_PATH environment variable is required');
  process.exit(1);
}

// Product model definition
const productSchema = new mongoose.Schema({
  name: String,
  price: Number,
  category: String,
  description: String,
  inStock: Boolean,
  createdAt: {
    type: Date,
    default: Date.now
  }
});

const Product = mongoose.model('Product', productSchema);

async function connectToDatabase() {
  try {
    await mongoose.connect(MONGODB_URI);
    console.log('Successfully connected to MongoDB');
  } catch (error) {
    console.error('Error connecting to MongoDB:', error);
    process.exit(1);
  }
}

async function retrieveAndFilterProducts() {
  try {
    console.log('Retrieving products from database...');

    // Find all products with price >= $10
    const products = await Product.find({ price: { $gte: 10 } });

    console.log(`Found ${products.length} products with price >= $10`);

    return products;
  } catch (error) {
    console.error('Error retrieving products:', error);
    throw error;
  }
}

async function saveToJSONFile(products) {
  try {
    // Ensure the directory exists
    const dirPath = path.dirname(OUTPUT_FILE_PATH);
    if (!fs.existsSync(dirPath)) {
      fs.mkdirSync(dirPath, { recursive: true });
      console.log(`Created directory: ${dirPath}`);
    }

    // Write filtered products to JSON file
    const jsonContent = JSON.stringify(products, null, 2);
    fs.writeFileSync(OUTPUT_FILE_PATH, jsonContent);

    console.log(`Successfully saved ${products.length} filtered products to ${OUTPUT_FILE_PATH}`);
  } catch (error) {
    console.error('Error saving to JSON file:', error);
    throw error;
  }
}

