        }
    }

    public void SetEnvironment(EnvironmentalCondition condition)
    {
        currentCondition = condition;
        Debug.Log($"Environment changed to: {condition}");
        UpdateUniforms();
    }

    public EnvironmentalCondition GetCurrentCondition()
    {
        return currentCondition;
    }

    private void UpdateUniforms()
    {
        // Trigger the update on all soldiers
        foreach (SoldierUnit soldier in FindObjectsOfType<SoldierUnit>())
        {
            soldier.UpdateUniformBasedOnCondition(currentCondition);
        }
    }
